using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;
using System.Web.Mvc;
using VendorMgmt.DataAccess;
using VendorMgmt.DataAccess.Model;

namespace VendorMgmt.Web.Controllers
{
    public class EmailTemplateController : Controller
    {
        // GET: EmailTemplate
        public ActionResult Index()
        {
            if (Request.Cookies["UserToken"] != null)
            {
                //You get the user's first and last name below:
                ViewBag.Name = Request.Cookies["UserName"]?.Value;
                ViewBag.UserGuid = Request.Cookies["UserGuid"]?.Value;
                // The 'preferred_username' claim can be used for showing the username
                ViewBag.Username = Request.Cookies["UserEmail"]?.Value;
            }
            else
            {
                return RedirectToAction("Index", "Home");
            }
            EmailTemplateViewModel model = new EmailTemplateViewModel();
            EmailTemplateService es = new EmailTemplateService();
            model.lstResults = es.EmailTemplates.ToList();
            return View(model);
        }

        public JsonResult Delete(int TemplateId)
        {
            EmailTemplateService es = new EmailTemplateService();
            es.DeleteEmailTemplatebyId(TemplateId);
            TempData["Message"] = "Email Template Deleted Successfully";
            return Json("Done", JsonRequestBehavior.AllowGet);
        }

        public ActionResult Add(int Id = 0)
        {
            EmailTemplateService es = new EmailTemplateService();
            EmailTemplate model = new EmailTemplate();
            if (Id > 0)
            {
                model = es.EmailTemplateById(Id);
            }
            return View(model);
        }
